import React, { Component, useState } from 'react'

import { Button, StyleSheet, Text, View, ScrollView, TextInput } from "react-native";
import NativeForms from "native-forms";
// import { Input, Label } from 'react-native-elements';
import DatePicker from 'react-native-datepicker'
import RadioButtonRN from 'radio-buttons-react-native';
import RadioForm, { RadioButton, RadioButtonInput, RadioButtonLabel } from 'react-native-simple-radio-button';
//import { Dropdown } from 'react-native-material-dropdown';


export default class SignUpPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: '',
      lastName: '',
      date: "",
      phone_number: '',
      email: '',
      email_confirm: '',
      password: '',
      password_confirm: '',
      enableSecure: true,
      value: 0
    }
  }

  radio_props = [
    { label: "עיר ורדיוס", value: 0 },
    { label: "מיקום נוכחי ורדיוס", value: 1 }
  ];

  onChangeText = (key, val) => {
    this.setState({ [key]: val })
  }

  confirmEmail = () => { }


  render() {

    return (
      <ScrollView >
        <View style={styles.container}>

          <Text>שם פרטי</Text>
          <TextInput
            style={styles.input}
            autoCapitalize="none"
            onChangeText={val => this.onChangeText('firstName', val)}
          />

          <Text style={styles.Text}>שם משפחה</Text>
          <TextInput
            style={styles.input}
            secureTextEntry={true}
            autoCapitalize="none"
            onChangeText={val => this.onChangeText('lastName', val)}
          />

          <Text>תאריך לידה</Text>
          <DatePicker
            style={{ width: 200 }}
            date={this.state.date}
            mode="date"
            placeholder="select date"
            format="DD-MM-YYYY"
            minDate="01-01-1940"
            maxDate="01-01-2006"
            confirmBtnText="Confirm"
            cancelBtnText="Cancel"
            customStyles={{
              dateIcon: {
                position: 'absolute',
                left: 0,
                top: 4,
                marginLeft: 0
              },
              dateInput: {
                marginLeft: 36
              }
            }}
            onChangeText={val => this.onChangeText('date', val)}
          // onDateChange={(date) => { this.setState({ date: date }) }}
          />

          <Text>מספר טלפון</Text>
          <TextInput
            style={styles.input}
            autoCapitalize="none"
            onChangeText={val => this.onChangeText('phone_number', val)}
          />

          {/* לעשות פונצקיה של אימות מייל וסיסמה, בדיקה של הזנה נכונה של מייל */}

          <Text>אימייל</Text>
          <TextInput
            style={styles.input}
            placeholder='example@email.com'
            autoCapitalize="none"
            placeholderTextColor='#A7A7A7'
            onChangeText={val => this.onChangeText('email', val)}
          />

          <Text>אימות אימייל</Text>
          <TextInput
            style={styles.input}
            placeholder='example@email.com'
            autoCapitalize="none"
            placeholderTextColor='#A7A7A7'
            onChangeText={val => this.onChangeText('email_confirm', val)}
          />

          <Text>סיסמה</Text>
          <TextInput
            style={styles.input}
            secureTextEntry={this.state.enableSecure}
            autoCapitalize="none"
            placeholderTextColor='#A7A7A7'
            onChangeText={val => this.onChangeText('password', val)}
          />

          <Text>אימות סיסמה</Text>
          <TextInput
            style={styles.input}
            secureTextEntry={this.state.enableSecure}
            autoCapitalize="none"
            placeholderTextColor='#A7A7A7'
            onChangeText={val => this.onChangeText('password_confirm', val)}
          />

          <RadioForm
            formHorizontal={true}
            animation={true}
          >
            {this.radio_props.map((label, value) => (
              <RadioButton labelHorizontal={true} key={value} >
                <RadioButtonInput
                  obj={label}
                  index={value}
                  isSelected={this.state.value === value}
                  onPress={(value) => { this.setState({ value: value }) }}
                  borderWidth={1}
                  buttonColor={'#000000'}
                  //buttonOuterColor={this.state.value === i ? '#2196f3' : '#000'}
                  buttonSize={15}
                  //buttonOuterSize={80}
                  //buttonStyle={{}}
                  buttonWrapStyle={{ margin: 5 }}
                />
                <RadioButtonLabel
                  obj={label}
                  index={value}
                  labelHorizontal={true}
                  //onPress={(value) => { this.setState({ value: value }) }}
                  labelStyle={{ fontSize: 14, color: '#000000' }}
                //labelWrapStyle={{}}
                />
              </RadioButton>))}
          </RadioForm>


          <Button
            title='הרשמה'
            onPress={this.signUp}
          />
        </View>
      </ScrollView >
    );
  }
}
// const styles = StyleSheet.create({
//     container: {
//         flex: 2,
//         backgroundColor: "#fff",
//         alignItems:"center",
//         justifyContent: "center",

//     }
// });
const styles = StyleSheet.create({
  input: {
    width: 300,
    height: 55,
    backgroundColor: '#fff',
    margin: 3,
    padding: 8,
    color: '#414042',
    borderRadius: 14,
    fontSize: 14,
    fontWeight: '500'
  },
  Text: {
    fontSize: 14,
    fontWeight: '500'
  },
  container: {
    flex: 1,
    justifyContent: 'space-evenly',
    alignItems: 'center'
  }
})
