import React, { Component } from 'react'
import { Text, View, StyleSheet, TouchableOpacity, TextInput, ImageBackground, Image } from 'react-native'

var tempUser = '';
export default class LogInPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      checkU: false
    }
  }

  btnSignUp = () => {
    this.props.navigation.navigate('Sign Up')
  }

  btnLogIn = () => {

    const url = "http://proj.ruppin.ac.il/bgroup17/prod/api/User";
    fetch(url, {
      method: 'GET',
      //body: JSON.stringify(),
      headers: new Headers({
        'Content-Type': 'application/json; charset=UTF-8',
        'Accept': 'application/json; charset=UTF-8',
      })
    })
      .then(res => {
        console.log('res.ok=', res.ok);
        return res.json()
      })
      .then(userArr => {
        // for (let i = 0; i < userArr.length; i++) {
        //   if (userArr[i].email == this.state.email && userArr[i].password == this.state.password) {
        //     //console.log("uers",userArr)
        //     this.setState({ checkU: true },()=>
        //     {
        //       if (checkU) {
        //         alert("log in")
        //         console.log('check user state: ', checkU)

        //       }
        //       else {
        //         alert("not good")
        //       }
        //     })
        //   }
        // }
        //  this.setState({ dataArr: userArr }, () => {
        for (let i = 0; i < userArr.length; i++) {
          if (userArr[i].email == this.state.email) {
            tempUser = userArr[i];
          }
        }
        if (tempUser.password == this.state.password) {
          // alert("log in")
          console.log('tempUser: ', tempUser)
          this.props.navigation.navigate('FeedPage');
        }
        else {
          alert("not good")
        }
        // })

      },
        (error) => {
          console.log('Error', error);
        })
  }
  loginp = () => {
    for (let i = 0; i < this.state.dataArr.length; i++) {
      if (this.state.dataArr[i].email == this.state.email) {
        tempUser = this.state.dataArr[i];
      }
    }
    if (tempUser.password == this.state.password) {
      alert("log in")
      console.log('tempUser: ', tempUser)

    }
    else {
      alert("not good")
    }
  }
  render() {
    return (
      <ImageBackground source={require('../../Swish_09.03/assets/bgImage.png')} style={styles.image}>

        <View style={styles.container}>
          <Image source={require('../../Swish_09.03/assets/logo.png')} style={styles.logo} />
          <Text></Text>
          <View style={styles.inputView} >
            <TextInput
              style={styles.inputText}
              placeholder="אימייל"
              placeholderTextColor="#A7A7A7"
              onChangeText={text => this.setState({ email: text })} />
          </View>
          <View style={styles.inputView} >
            <TextInput
              secureTextEntry
              style={styles.inputText}
              placeholder="סיסמה"
              placeholderTextColor="#A7A7A7"
              onChangeText={text => this.setState({ password: text })} />
          </View>
          <TouchableOpacity>
            <Text style={styles.forgot}>שכחת סיסמה?</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.loginBtn} onPress={this.btnLogIn}>
            <Text style={styles.loginText}>התחברות</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.regBtn} onPress={this.btnSignUp}>
            <Text style={styles.regText}>הרשמה</Text>
          </TouchableOpacity>

        </View>
      </ImageBackground>
    )
  }
}

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
    justifyContent: 'center',
    width: "100%",
  },
  image: {
    flex: 1,
    resizeMode: 'cover',
    justifyContent: 'center',
    alignItems: 'center'
  },
  inputView: {
    width: "80%",
    backgroundColor: "#FFF",
    borderWidth: 1,
    borderColor: "#A7A7A7",
    borderRadius: 10,
    height: 50,
    marginBottom: 20,
    justifyContent: "center",
    padding: 20
  },
  inputText: {
    height: 50,
    color: "#101010"
  },
  loginText: {
    color: "white",
    fontSize: 18
  },
  regText: {
    color: "white",
    fontSize: 14
  },
  forgot: {
    color: "#101010",
    fontSize: 14
  },
  loginBtn: {
    width: "80%",
    backgroundColor: "#7DA476",
    borderRadius: 10,
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    marginTop: 40,
    marginBottom: 10
  },
  regBtn: {
    width: 80,
    backgroundColor: "#9d76a5",
    borderRadius: 10,
    height: 40,
    alignItems: "center",
    justifyContent: "center",
    marginTop: 40,
    marginBottom: 10
  },
  logo: {
    width: 300,
    height: 150
  }
})
