import React, { Component, useState } from 'react'

import { Button, StyleSheet, Text, View } from "react-native";
import NativeForms from "native-forms";
import { Input,Label } from 'react-native-elements';

// const App = () => {
// //   const [hasForm, showForm] = useState(false);
//   const show = () => showForm(true);
//   const hide = () => showForm(false);
// }
export default class SignUpPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            hasForm: false,
            showForm: false
        }
    }

    show = () => { this.setState({ showForm: true }) };
    hide = () => { this.setState({ showForm: false }) };
    render() {
        return (
            <View style={styles.container}>
                <Text>NativeForms.com</Text>
                <Button title="Show Form" onPress={this.show} color="#20f" />

                {/* {this.show} */}
                {this.state.hasForm && (
                    <NativeForms
                        form="https://my.nativeforms.com/vVDct0mcvZWPmZic4JlRvpmNy0Db"
                        onClose={this.hide}
                    />
                )}
                <Input
                    // placeholder='BASIC INPUT'
                    label="שם פרטי"
                />
                 <Input
                    // placeholder='BASIC INPUT'
                    label="שם משפחה"
                    style={width}
                />
            </View>
        );
    }
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#fff",
        alignItems: "center",
        justifyContent: "center"
    }
});
