import React, { Component, useState } from 'react'

import { Button, StyleSheet, Text, View, ScrollView, TextInput, TouchableOpacity, Image, ImageBackground } from "react-native";
import DatePicker from 'react-native-datepicker'
import RadioForm, { RadioButton, RadioButtonInput, RadioButtonLabel } from 'react-native-simple-radio-button';
import DropDownPicker from 'react-native-dropdown-picker';
import { Slider } from 'react-native-elements';
import Icon from 'react-native-vector-icons/FontAwesome';

export default class SignUpPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: '',
      firstName: '',
      lastName: '',
      date: "",
      phone_number: '',
      email: '',
      email_confirm: '',
      password: '',
      password_confirm: '',
      profilePicture: '',
      enableSecure: true,
      value_radio: 0,
      radius: 3,
      valueSlider: '',
      itemViewingMethod: '',
      country: '',
      uri: ''
    }
  }
componentDidMount(){
  this._unsubscribeFocus = this.props.navigation.addListener('Sign Up', (index) => {
    this.setState({uri:this.props.route.params})
    console.log("stam: ",this.props.route.params)
  })
}
componentWillUnmount(){
  this._unsubscribeFocus();
}
  radio_props = [
    { label: "עיר ורדיוס", value: 0 },
    { label: "מיקום נוכחי ורדיוס", value: 1 }
  ];


  onChangeText = (key, val) => {
    this.setState({ [key]: val })
  }

  confirmEmail = () => {
    if (this.state.email != '' && this.state.email_confirm != '') {
      let emailAddress = this.state.email;
      let emailAddConfirm = this.state.email_confirm;
      let sign = '@';
      let dot = '.';
      let Tsign = emailAddress.includes(sign);
      let Tdot = emailAddress.includes(dot);

      if (Tsign && Tdot) {
        if (emailAddress != emailAddConfirm) {
          alert('אימות כתובת האימייל לא צלחה, נסה שנית')
        }
      }
      else {
        alert('כתובת האימייל לא תקינה')
      }
    }
  }

  confirmPassword = () => {
    if (this.state.password != '' && this.state.password_confirm != '') {
      let password = this.state.password;
      let passwordConfirm = this.state.password_confirm
      let check = this.checkAlphaNum(password);
      console.log('check: ', check)
      if (check.containsNumber) {
        if (password != passwordConfirm) {
          alert('אימות הסיסמה לא צלח, נסה שנית')
        }
      }
      else {
        alert('הסיסמה לא תקינה, נדרשת להכיל מספרים')
      }
    }
  }

  checkAlphaNum = (password) => {
    let exp = {
      containsNumber: /\d+/
    };
    let expMatch = {};
    expMatch.containsNumber = exp.containsNumber.test(password);
    console.log('exp- numbers: ', expMatch)
    return expMatch;
  }

  radioBtnValueRL = (value) => {
    this.setState({ value_radio: value })
    if (value == 0) {
      this.setState({ itemViewingMethod: 'R' })
    }
    else {
      this.setState({ itemViewingMethod: 'L' })
    }
  }

  signUpBtn = () => {
    this.confirmEmail();
    this.confirmPassword();
    const urlSignUp = "http://proj.ruppin.ac.il/bgroup17/prod/api/User";

    let newUser = {
      id: this.state.id,
      firstName: this.state.firstName,
      lastName: this.state.lastName,
      birthDate: this.state.date,
      phoneNumber: this.state.phone_number,
      email: this.state.email,
      password: this.state.password,
      residence: this.state.country,
      radius: this.state.radius,
      profilePicture: this.state.profilePicture,
      itemViewingMethod: this.state.itemViewingMethod
    }
    console.log(newUser); //לבדוק שהטקסט מהשדות נקלט 

    fetch(urlSignUp, {
      method: 'POST',
      body: JSON.stringify(newUser),
      headers: new Headers({
        'Content-Type': 'application/json; charset=UTF-8',
        'Accept': 'application/json; charset=UTF-8',
      })
    })
      .then(res => {
        console.log( 'res: ',res)
        console.log('res.ok=', res.ok);
        return res.json()
      })
      .then(i => {
        alert("user added")
      },
        (error) => {
          console.log('Error', error);
        })
  }
  
  btnBack = () => {
    this.props.navigation.navigate('LogIn')
  }
  render() {

    return (
      <ScrollView>
        <ImageBackground source={require('../../Swish_09.03/assets/bgImage.png')} style={styles.image}>

            <TouchableOpacity onPress={this.btnBack} style={styles.backBtn}>
              <Icon name="chevron-left" size={20} color="#101010" />
            </TouchableOpacity>
            <View style={{alignItems: 'center', justifyContent: 'center'}}>
              <Text style={{ fontSize: 30 }}>הרשמה</Text>
            </View>

          <View style={styles.container}>
            {/* <View style={{ flexDirection: 'row', padding: 10 }}> */}
            <View style={{ flex: 1 }}>
              <Text>שם פרטי</Text>
              <TextInput
                style={styles.input}
                autoCapitalize="none"
                onChangeText={val => this.onChangeText('firstName', val)}
              />
            </View>
            <View style={{ flex: 1, }}>
              <Text style={styles.Text}>שם משפחה</Text>
              <TextInput
                style={styles.input}
                autoCapitalize="none"
                onChangeText={val => this.onChangeText('lastName', val)}
              />
            </View>
            {/* </View> */}

            <Text>תאריך לידה</Text>
            <DatePicker
              style={{ width: 200 }}
              date={this.state.date}
              mode="date"
              placeholder="select date"
              format="DD-MM-YYYY"
              minDate="01-01-1940"
              maxDate="01-01-2006"
              confirmBtnText="Confirm"
              cancelBtnText="Cancel"
              customStyles={{
                dateIcon: {
                  position: 'absolute',
                  left: 0,
                  top: 4,
                  marginLeft: 0
                },
                dateInput: {
                  marginLeft: 36
                }
              }}
              onChangeText={val => this.onChangeText('date', val)}
              onDateChange={(date) => { this.setState({ date: date }) }}
            />

            <Text>מספר טלפון</Text>
            <TextInput
              style={styles.input}
              autoCapitalize="none"
              onChangeText={val => this.onChangeText('phone_number', val)}
            />

            {/* לעשות פונצקיה של אימות מייל וסיסמה, בדיקה של הזנה נכונה של מייל */}

            <Text>אימייל</Text>
            <TextInput
              style={styles.input}
              placeholder='example@email.com'
              autoCapitalize="none"
              placeholderTextColor='#A7A7A7'
              onChangeText={val => this.onChangeText('email', val)}
            />

            <Text>אימות אימייל</Text>
            <TextInput
              style={styles.input}
              placeholder='example@email.com'
              autoCapitalize="none"
              placeholderTextColor='#A7A7A7'
              onChangeText={val => this.onChangeText('email_confirm', val)}
            />

            <Text>סיסמה</Text>
            <TextInput
              style={styles.input}
              secureTextEntry={this.state.enableSecure}
              autoCapitalize="none"
              placeholderTextColor='#A7A7A7'
              onChangeText={val => this.onChangeText('password', val)}
            />

            <Text>אימות סיסמה</Text>
            <TextInput
              style={styles.input}
              secureTextEntry={this.state.enableSecure}
              autoCapitalize="none"
              placeholderTextColor='#A7A7A7'
              onChangeText={val => this.onChangeText('password_confirm', val)}
            />

            <View style={styles.radioBtn}>
              <Text>תצוגת פריטים לפי:</Text>
              <Text></Text>
              <RadioForm
                formHorizontal={true}
                animation={true}
              >
                {this.radio_props.map((label, value) => (
                  <RadioButton labelHorizontal={false} key={value} >
                    <RadioButtonLabel
                      obj={label}
                      index={value}
                      labelStyle={{ fontSize: 14, color: '#000000' }}
                    />
                    <RadioButtonInput
                      obj={label}
                      index={value}
                      isSelected={this.state.value_radio === value}
                      onPress={(value) => { this.radioBtnValueRL(value) }}
                      borderWidth={1}
                      buttonColor={'#000000'}
                      buttonSize={15}
                      buttonWrapStyle={{ margin: 5 }}
                    />
                  </RadioButton>))}
              </RadioForm>
            </View>

            <View>
              <DropDownPicker
                items={[
                  { label: 'הרצליה', value: 'הרצליה' },
                  { label: 'תל- אביב', value: 'תל- אביב' },
                  { label: 'רעננה', value: 'r' },
                  { label: 'רעננה', value: 't' },
                  { label: 'רעננה', value: 'y' },
                  { label: 'רעננה', value: 'f' },
                  { label: 'רעננה', value: 'g' },
                  { label: 'רעננה', value: 'b' },
                  { label: 'רעננה', value: 'v' },
                  { label: 'רעננה', value: 'a' },
                  { label: 'רעננה', value: 's' },
                ]}
                containerStyle={{ height: 40, width: 200 }}
                style={{ backgroundColor: '#fafafa', width: 200, paddingVertical: 10 }}
                itemStyle={{ justifyContent: 'flex-end' }}
                dropDownStyle={{ backgroundColor: '#fafafa' }}
                onChangeItem={item => this.setState({ country: item.value })}
                arrowStyle={{ margin: 10 }}
              />
            </View>

            <View style={{ alignItems: 'stretch', justifyContent: 'center', width: 300 }}>
              <Slider
                onValueChange={(value) => this.setState({ radius: value })}
                maximumValue={40}
                minimumValue={0}
                step={1}
                trackStyle={{ height: 10, backgroundColor: 'transparent' }}
                animateTransitions={true}
                thumbStyle={{ height: 30, width: 30, backgroundColor: '#7DA476' }}
              />
              <Text>
                רדיוס: {this.state.radius}
              </Text>
            </View>

            <Text>העלאת תמונת פרופיל</Text>
            <Button
              title='העלאת תמונה'
              onPress={() => { this.props.navigation.push('Gallery') }}
              color='#7DA476'
            />
            <View>             
              {console.log('props: ', this.state.uri)}
              {this.state.uri =! undefined ? 
                <Image source={{ uri: this.state.uri }} style={styles.profileImage}></Image>:<Image></Image>}
            </View>
            <Text></Text>

            <Button
              title='הרשמה'
              onPress={this.signUpBtn}
              color='#9D76A5'
            />
          </View>

        </ImageBackground>
      </ScrollView >
    );
  }
}

